package com.devvailonge.tvshows.home

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.RecyclerView
import com.devvailonge.home.R

class HomeFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private var categoryList = getCategories()
    private val adapter = CategoryAdapter(::categoryClickListener)

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        // Inflate the layout for this fragment
        val root = inflater.inflate(R.layout.fragment_home, container, false)
        recyclerView = root.findViewById(R.id.rvCategories)
        return root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)


        recyclerView.adapter = adapter
        adapter.updateList(categoryList)

    }

    private fun categoryClickListener(category: Category) {
        val newList = categoryList.map {
            if (it.category == category.category){
                it.copy(isSelected = category.isSelected)
            }else{
                it
            }
        }
        categoryList = newList
        adapter.updateList(categoryList)
    }

    private fun getCategories (): List<Category> {
    return arrayListOf(
        Category("Action"),
        Category("Comedy"),
        Category("Crime"),
        Category("Adventure"),
        Category("Fantasy"),
        Category("Science Fiction"),
        Category("Suspense"),
        Category("Drama"),
        Category("Thriller"),
        Category("Horror"),
        Category("Romance"),
        Category("Children"),
        Category("Documentary"),
        Category("Family")
        ).toList()
    }

    data class Category (val category: String, var isSelected: Boolean = false )

}


private fun getFilms () : List<Film> {
    return arrayListOf(

        Film("The Batman", "data:image/jpeg;base64,"),
        Film("Fast & Furious", "data:image/jpeg;base64,"),
        Film("James Bond: No Time To Die", "data:image/jpeg;base64,"),
        Film("Top Gun 2", "data:image/jpeg;base64,"),
        Film("Dune", ""),
        Film("Justice League", ""),
        Film("Black Mirror", ""),
        Film("The King's Man", ""),
        Film("Black Adam", ""),
        Film("The Suicide Squad", ""),
        Film("Rumble", ""),
        Film("The Little Things", ""),




    ).toList()
}


data class Film (val nome: String, val image: String )



